const text = $('List of briefes').first().json.text;
const righe = text.split('\n').map(r => r.trim());

// Struttura dati iniziale
const data = {
  PromptChatGPT: "",
  IB: { Titolo: "", Testo: "", Br: "" },
  DW: { Titolo: "", Testo: "", Br: "" },
  EP: { Titolo: "", Testo: "", Br: "" },
  IMPIANTI: { Titolo: "" },
  SICUREZZA: { Titolo: "", Testo: "", Br: "" },
  EX: { Titolo: "", Testo: "", Br: "" },
  CORA: { Titolo: "", Testo: "", Br: "" },
  TAG: [],
  TagAggiuntivi: []
};

let currentBlock = null;
let currentField = null;
let parsingTags = false;
let parsingExtraTags = false;

// flag per sapere se stiamo raccogliendo il prompt
let collectingPrompt = false;

for (const riga of righe) {
  if (riga.startsWith("Prompt ChatGPT:")) {
    collectingPrompt = true;
    currentBlock = "Prompt";
    data.PromptChatGPT = riga.replace("Prompt ChatGPT:", "").trim();
    parsingTags = false;
    parsingExtraTags = false;
  } 
  else if (collectingPrompt) {
    if (riga === "" || /^(IB|DW|EP|IMPIANTI|SICUREZZA|EX|CORA):$/.test(riga)) {
      // smettiamo di raccogliere quando troviamo riga vuota o un nuovo blocco
      collectingPrompt = false;
    } else {
      data.PromptChatGPT += " " + riga;
    }
  }
  else if (/^(IB|DW|EP|IMPIANTI|SICUREZZA|EX|CORA):$/.test(riga)) {
    currentBlock = riga.replace(":", "").trim();
    currentField = null;
    parsingTags = false;
    parsingExtraTags = false;
  } 
  else if (riga.startsWith("Titolo:")) {
    currentField = "Titolo";
    if (currentBlock && data[currentBlock]) {
      data[currentBlock].Titolo = riga.replace("Titolo:", "").trim();
    }
  } 
  else if (riga.startsWith("Testo:")) {
    currentField = "Testo";
    if (currentBlock && data[currentBlock]) {
      data[currentBlock].Testo = riga.replace("Testo:", "").trim();
    }
  } 
  else if (riga.startsWith("<br>")) {
    // Br resta vuoto, solo segnaposto
    currentField = "Br";
  }
  else if (riga.startsWith("@TAG:")) {
    parsingTags = true;
    parsingExtraTags = false;
    currentBlock = null;
  }
  else if (riga.startsWith("Tag aggiuntivi")) {
    parsingTags = false;
    parsingExtraTags = true;
    currentBlock = null;
  }
  else if (parsingTags && riga.length > 0) {
    data.TAG.push(riga);
  }
  else if (parsingExtraTags && riga.startsWith("-")) {
    data.TagAggiuntivi.push(riga.replace("-", "").trim());
  }
  else if (riga.length > 0 && currentBlock && currentField) {
    if (currentField !== "Br") {
      data[currentBlock][currentField] += (data[currentBlock][currentField] ? " " : "") + riga;
    }
  }
}

// Restituisce JSON strutturato
