const inputText = $input.first().json.text|| '';

// Dividi il testo in righe
const lines = inputText.split('\n');

// Filtra le righe dove c'è un tag non vuoto dopo i due punti
const filteredLines = lines.filter(line => {
  const parts = line.split(':');
  return parts.length === 2 && parts[1].trim() !== '';
});

// Ricostruisci il testo filtrato
const outputText = filteredLines.join('\n');

// Restituisci il risultato per il nodo successivo
return {
  json: {
    cleanedTags: outputText
  }
};

